    // Calculate the scale factor to fit the path within the component bounds
    // while maintaining the aspect ratio
    float scaleX = componentBounds.getWidth() / pathBounds.getWidth();
    float scaleY = componentBounds.getHeight() / pathBounds.getHeight();
    float scaleFactor = std::min(scaleX, scaleY);
    
    // Calculate the translation needed to center the path
    float offsetX = (componentBounds.getWidth() - pathBounds.getWidth() * scaleFactor) / 2.0f;
    float offsetY = (componentBounds.getHeight() - pathBounds.getHeight() * scaleFactor) / 2.0f;

    // Apply the scale and translation to the path
    path.applyTransform(juce::AffineTransform::scale(scaleFactor).translated(offsetX, offsetY));

    // Set the colour and draw the path
    g.setColour(logoColour);

